import { useState } from "react";
import { FaTimes, FaChevronLeft, FaChevronRight, FaFacebook, FaWhatsapp, FaPhone } from "react-icons/fa";

const photos = [
  { src: "/images/pho1.jpg", caption: "विद्यालय उद्घाटन के समय बच्चों के साथ" },
  { src: "/images/pho2.jpg", caption: "स्वास्थ्य शिविर का आयोजन" },
  { src: "/images/pho3.jpg", caption: "सड़क निर्माण निरीक्षण के दौरान" },
  { src: "/images/pho4.jpg", caption: "महिला स्व-सहायता समूह से संवाद" },
  { src: "/images/pho5.jpg", caption: "त्योहार पर आशीर्वाद लेते हुए" },
  { src: "/images/pho6.jpg", caption: "जनता दरबार में समस्या सुनते हुए" },
  { src: "/images/pho7.jpg", caption: "युवाओं को रोजगार दिलाते हुए" },
  { src: "/images/pho8.jpg", caption: "ऑनलाइन सेवा शिविर का आयोजन" },
];

const testimonials = [
  {
    name: "रामेश्वर प्रसाद",
    comment: "पहली बार कोई नेता इतना ज़मीन से जुड़ा हुआ दिखा। हर समय मदद के लिए तैयार रहते हैं।",
  },
  {
    name: "सुनीता देवी",
    comment: "हमारे मोहल्ले की सड़क सालों से टूटी थी, लेकिन इन्हीं के प्रयास से बन पाई।",
  },
  {
    name: "मोहन कुमार",
    comment: "मेरी मां के इलाज में इन्होंने खुद मदद की। ऐसे ही लोग असली जनसेवक होते हैं।",
  },
];

export default function App() {
  const [selected, setSelected] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const openModal = (index) => {
    setCurrentIndex(index);
    setSelected(photos[index]);
  };

  const nextSlide = () => {
    const newIndex = (currentIndex + 1) % photos.length;
    setCurrentIndex(newIndex);
    setSelected(photos[newIndex]);
  };

  const prevSlide = () => {
    const newIndex = (currentIndex - 1 + photos.length) % photos.length;
    setCurrentIndex(newIndex);
    setSelected(photos[newIndex]);
  };

  return (
    <div className="font-sans text-gray-800">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-orange-400 via-white to-green-400 text-center py-20">
        <h1 className="text-4xl md:text-5xl font-bold mb-4">🙏 जनता की सेवा, हमारा संकल्प 🙏</h1>
        <p className="text-lg md:text-xl">हर गली, हर घर तक विकास पहुँचाना ही हमारा उद्देश्य है।</p>
      </section>

      {/* Gallery Section */}
      <section className="py-16 bg-gradient-to-b from-amber-50 to-orange-100">
        <div className="max-w-6xl mx-auto px-4">
          <div className="text-center mb-10">
            <h2 className="text-3xl font-bold text-gray-800">हमारी झलकियाँ 📸</h2>
            <p className="text-gray-600 mt-2">हर पल जनता के बीच, हर काम जनता के लिए</p>
          </div>

          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {photos.map((photo, index) => (
              <div
                key={index}
                className="relative group cursor-pointer"
                onClick={() => openModal(index)}
              >
                <img
                  src={photo.src}
                  alt={photo.caption}
