import { useState } from "react";
import { App } from "./components/Gallery";

const photos = [
  {
    src: "/images/photo1.jpg",
    caption: "विद्यालय उद्घाटन के समय बच्चों के साथ",
  },
  {
    src: "/images/photo1.jpg",
    caption: "स्वास्थ्य शिविर का आयोजन",
  },
  {
    src: "/images/photo1.jpg",
    caption: "सड़क निर्माण निरीक्षण के दौरान",
  },
  {
    src: "/images/photo1.jpg",
    caption: "महिला स्व-सहायता समूह से संवाद",
  },
  {
    src: "/images/photo1.jpg",
    caption: "त्योहार पर आशीर्वाद लेते हुए",
  },
  // aur bhi add karte jao...
];

export default function Gallery() {
  const [selected, setSelected] = useState(null);

  return (
    <section className="py-16 bg-gradient-to-b from-amber-50 to-orange-100">
      <div className="max-w-6xl mx-auto px-4">
        {/* Heading */}
        <div className="text-center mb-10">
          <h2 className="text-3xl font-bold text-gray-800">
            हमारी झलकियाँ 📸
          </h2>
          <p className="text-gray-600 mt-2">
            हर पल जनता के बीच, हर काम जनता के लिए
          </p>
        </div>

        {/* Gallery Grid */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
          {photos.map((photo, index) => (
            <div
              key={index}
              className="relative group cursor-pointer"
              onClick={() => setSelected(photo)}
            >
              <img
                src={photo.src}
                alt={photo.caption}
                className="rounded-xl shadow-md group-hover:scale-105 transition-transform duration-300"
              />
              <div className="absolute bottom-2 left-2 right-2 bg-black bg-opacity-50 text-white text-sm p-1 rounded-md opacity-0 group-hover:opacity-100 transition-opacity">
                {photo.caption}
              </div>
            </div>
          ))}
        </div>

        {/* Modal (Full Image View) */}
        {selected && (
          <div
            className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
            onClick={() => setSelected(null)}
          >
            <div className="max-w-3xl max-h-[90vh]">
              <img
                src={selected.src}
                alt={selected.caption}
                className="rounded-xl shadow-lg"
              />
              <p className="text-white text-center mt-3">{selected.caption}</p>
            </div>
          </div>
        )}

        {/* Closing Note */}
        <div className="text-center mt-12">
          <p className="text-lg text-gray-700 italic">
            “यह सिर्फ़ तस्वीरें नहीं, यह हमारे रिश्तों की कहानी है। जनता के हर दुख-सुख में हमेशा साथ।”  
          </p>
        </div>
      </div>
    </section>


  );
}


export  function website() {
  // 📸 Photos array
  const photos = [
    { src: "/images/photo1.jpg", caption: "विद्यालय उद्घाटन के समय बच्चों के साथ" },
    { src: "/images/photo2.jpg", caption: "स्वास्थ्य शिविर का आयोजन" },
    { src: "/images/photo3.jpg", caption: "सड़क निर्माण निरीक्षण के दौरान" },
    { src: "/images/photo4.jpg", caption: "महिला स्व-सहायता समूह से संवाद" }
  ];

  // 📝 Feedback state
  const [feedbacks, setFeedbacks] = useState([
    "मुखिया जी ने बहुत मदद की, धन्यवाद!",
    "गाँव में सड़क और पानी की व्यवस्था काफी सुधरी है।"
  ]);
  const [newFeedback, setNewFeedback] = useState("");

  // Feedback submit function
  const handleSubmit = (e) => {
    e.preventDefault();
    if (newFeedback.trim() !== "") {
      setFeedbacks([newFeedback, ...feedbacks]);
      setNewFeedback("");
    }
  };

  return (
    <div style={{ fontFamily: "sans-serif", background: "#f4f9f9", padding: "20px" }}>
      {/* ---------------- Gallery Section ---------------- */}
      <h1 style={{ textAlign: "center", color: "#2c3e50" }}>📸 मुखिया जी की गैलरी</h1>
      <div style={{ display: "flex", gap: "20px", flexWrap: "wrap", justifyContent: "center" }}>
        {photos.map((photo, i) => (
          <div key={i} style={{
            border: "1px solid #ddd",
            borderRadius: "12px",
            padding: "10px",
            width: "250px",
            textAlign: "center",
            background: "white",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
            transition: "transform 0.3s",
          }}
          onMouseEnter={(e) => e.currentTarget.style.transform = "scale(1.05)"}
          onMouseLeave={(e) => e.currentTarget.style.transform = "scale(1)"}
          >
            <img src={photo.src} alt={`Photo ${i+1}`} style={{ width: "100%", borderRadius: "10px" }} />
            <p style={{ marginTop: "10px", fontWeight: "bold", color: "#34495e" }}>{photo.caption}</p>
          </div>
        ))}
      </div>

      {/* ---------------- Feedback Section ---------------- */}
      <h2 style={{ marginTop: "50px", textAlign: "center", color: "#16a085" }}>🗣️ जनता का आवाज़</h2>
      <form onSubmit={handleSubmit} style={{ textAlign: "center", marginBottom: "20px" }}>
        <textarea
          value={newFeedback}
          onChange={(e) => setNewFeedback(e.target.value)}
          placeholder="अपनी राय यहाँ लिखें..."
          style={{ width: "80%", padding: "10px", borderRadius: "8px", border: "1px solid #ccc" }}
        />
        <br />
        <button
          type="submit"
          style={{
            marginTop: "10px",
            padding: "10px 20px",
            borderRadius: "8px",
            border: "none",
            background: "#16a085",
            color: "white",
            cursor: "pointer"
          }}
        >
          भेजें
        </button>
      </form>

      {/* Feedback List */}
      <div style={{ maxWidth: "600px", margin: "0 auto" }}>
        {feedbacks.map((fb, i) => (
          <div key={i} style={{
            background: "white",
            margin: "10px 0",
            padding: "15px",
            borderRadius: "10px",
            boxShadow: "0px 2px 6px rgba(0,0,0,0.1)",
            animation: "fadeIn 0.5s"
          }}>
            {fb}
          </div>
        ))}
      </div>
    </div>
  );
}
